const storageType = localStorage;
const consentPropertyName = 'jdc_consent';

const shouldShowPopup = () => !storageType.getItem(consentPropertyName);
const saveToStorage = () => storage.setItem(consentPropertyName, true);

window.onload = () => {
    if (shouldShowPopup()){
        const consent = confirm('Agree to the terms');
        if (consent) {
            saveToStorage();
        }
    }
};

